#!/bin/bash
set -e

cd Dependencies/exr

rm IexConfig.h
rm IexConfigInternal.h
rm IlmThreadConfig.h
rm OpenEXRConfig.h
rm OpenEXRConfigInternal.h

$CMAKE_COMMAND . $CMAKE_OPTIONS -DCMAKE_INSTALL_PREFIX=$INSTALL_PREFIX -DBUILD_SHARED_LIBS=off -DCMAKE_BUILD_TYPE=$CMAKE_BUILD_TYPE -DBUILD_TESTING=off -DOPENEXR_BUILD_TOOLS=off -DOPENEXR_INSTALL_EXAMPLES=off $OPENEXR_OPTIONS -DCMAKE_CXX_FLAGS="$FLAGS"
$MAKE install
